import React, { useState, useEffect } from "react";
import { Question } from "../types";
import Result from "./Result";
import { saveQuizState, loadQuizState } from "../utils/storage";

interface QuizState {
  currentQuestion: number;
  score: number;
  timeLeft: number;
  answers: string[];
  isComplete: boolean;
  showResult: boolean;
  answerStatus: AnswerStatus[];
}

interface QuizProps {
  questions: Question[];
  timeLimit: number;
  timeSpent: number;
  onTryAgain: () => void;
}

interface AnswerStatus {
  selected: string;
  isCorrect: boolean;
}

const Quiz: React.FC<QuizProps> = ({ questions, timeLimit }) => {
  const [refreshKey, setRefreshKey] = useState(0);
  const [state, setState] = useState<QuizState>(() => {
    const saved = loadQuizState();
    return saved || {
      currentQuestion: 0,
      score: 0,
      timeLeft: timeLimit,
      answers: [],
      answerStatus: [],
      isComplete: false,
      showResult: false,
    };
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setState((prev) => {
        if (prev.timeLeft <= 0 || prev.isComplete) {
          clearInterval(timer);
          return { ...prev, isComplete: true, showResult: true };
        }
        return { ...prev, timeLeft: prev.timeLeft - 1 };
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [refreshKey]);

  useEffect(() => {
    saveQuizState(state);
  }, [state]);

  const handleAnswer = (answer: string) => {
    const isCorrect = answer === questions[state.currentQuestion].correctAnswer;
    setState((prev) => ({
      ...prev,
      score: isCorrect ? prev.score + 1 : prev.score,
      answers: [...prev.answers, answer],
      answerStatus: [...prev.answerStatus, { selected: answer, isCorrect }],
      currentQuestion: prev.currentQuestion + 1,
      isComplete: prev.currentQuestion + 1 >= questions.length,
      showResult: prev.currentQuestion + 1 >= questions.length,
    }));
  };

  const handlePrevious = () => {
    if (state.currentQuestion > 0) {
      setState(prev => ({
        ...prev,
        currentQuestion: prev.currentQuestion - 1,
        isComplete: false,
        showResult: false
      }));
    }
  };

  const handleRetry = () => {
    setState({
      currentQuestion: 0,
      score: 0,
      timeLeft: timeLimit,
      answers: [],
      answerStatus: [],
      isComplete: false,
      showResult: false,
    });
    setRefreshKey(prev => prev + 1);
    window.scrollTo(0, 0);
  };

  if (state.showResult || state.timeLeft <= 0) {
    const timeSpentSeconds = timeLimit - state.timeLeft;
    const actualTimeSpent = state.timeLeft <= 0 ? timeLimit : timeSpentSeconds;

    return (
      <Result
        score={state.score}
        totalQuestions={questions.length}
        answeredQuestions={state.answers.length}
        timeSpent={actualTimeSpent}
        onRetry={handleRetry}
      />
    );
  }

  return (
    <div className="space-y-6 bg-white p-6 rounded-2xl shadow-xl">
      <div className="flex justify-between items-center text-sm text-gray-600">
        <div className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full font-semibold">
          Question {state.currentQuestion + 1} / {questions.length}
        </div>
        <div className="bg-red-100 text-red-600 px-3 py-1 rounded-full font-semibold">
          Time Left: {Math.floor(state.timeLeft / 60)}:{(state.timeLeft % 60).toString().padStart(2, '0')}
        </div>
      </div>

      <div className="text-2xl font-semibold text-gray-800">
        {questions[state.currentQuestion].question}
      </div>

      <div className="grid grid-cols-1 gap-4">
        {questions[state.currentQuestion].options.map((option) => {
          const previousAnswer = state.answerStatus[state.currentQuestion];
          const isAnswered = Boolean(previousAnswer);
          const isSelected = previousAnswer?.selected === option;
          const isCorrect = questions[state.currentQuestion].correctAnswer === option;

          let buttonStyle = "w-full text-left px-4 py-3 rounded-xl transition-all duration-200 font-medium shadow-sm flex items-center justify-between ";

          if (isAnswered) {
            if (isSelected) {
              buttonStyle += isCorrect 
                ? "bg-green-100 border-green-300 text-green-700 " 
                : "bg-red-100 border-red-300 text-red-700 ";
            } else if (isCorrect) {
              buttonStyle += "bg-green-50 border-green-200 text-green-600 ";
            } else {
              buttonStyle += "bg-gray-50 border-gray-200 text-gray-500 ";
            }
          } else {
            buttonStyle += "bg-gradient-to-r from-blue-50 to-blue-100 border border-blue-200 hover:from-blue-100 hover:to-blue-200 text-gray-700 ";
          }

          return (
            <button
              key={option}
              onClick={() => !isAnswered && handleAnswer(option)}
              disabled={isAnswered}
              className={buttonStyle}
            >
              <span className="flex-grow">{option}</span>
              {isAnswered && (
                <span className="ml-2">
                  {isSelected && (
                    <span className={isCorrect ? "text-green-600" : "text-red-600"}>
                      {isCorrect ? "✓" : "✗"}
                    </span>
                  )}
                  {!isSelected && isCorrect && (
                    <span className="text-green-600">✓</span>
                  )}
                </span>
              )}
            </button>
          );
        })}
      </div>

      <div className="flex justify-between mt-4">
        <button
          onClick={handlePrevious}
          disabled={state.currentQuestion === 0}
          className={`px-4 py-2 rounded-lg ${
            state.currentQuestion === 0
              ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
              : 'bg-blue-500 text-white hover:bg-blue-600'
          } transition-colors duration-200`}
        >
          Previous Question
        </button>
        <div className="flex-grow"></div>
      </div>
    </div>
  );
};

export default Quiz;
