import React, { useState } from 'react';

interface ResultProps {
  score: number;
  totalQuestions: number;
  answeredQuestions: number;
  timeSpent: number;
  onRetry: () => void;
  onLogout: () => void;
}

const Result: React.FC<ResultProps> = ({ 
  score, 
  totalQuestions, 
  answeredQuestions, 
  timeSpent, 
  onRetry,
  onLogout 
}) => {
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  const handleLogout = async () => {
    // Clear all quiz-related data from localStorage
    const keysToRemove = [
      'quizProgress',
      'timeSpent',
      'answers',
      'score',
      'currentQuestion',
      'answerStatus',
      'isComplete',
      'showResult',
      'user'
    ];

    try {
      // Clear all quiz data
      keysToRemove.forEach(key => localStorage.removeItem(key));

      // Reset session storage if any
      sessionStorage.clear();

      // Clear any additional browser storage
      const clearBrowserData = async () => {
        if ('caches' in window) {
          try {
            const cacheNames = await caches.keys();
            await Promise.all(
              cacheNames.map(cacheName => caches.delete(cacheName))
            );
          } catch (error) {
            console.error('Error clearing cache:', error);
          }
        }
      };

      await clearBrowserData();

      // Call the provided logout handler
      onLogout();
    } catch (error) {
      console.error('Error during logout cleanup:', error);
      // Ensure logout happens even if cleanup fails
      onLogout();
    } finally {
      setIsLoggingOut(false);
    }
  };

  const formattedTime = `${Math.floor(timeSpent / 60)}:${(timeSpent % 60).toString().padStart(2, '0')}`;
  const isPerfectScore = score === totalQuestions;

  return (
    <div className="text-center space-y-6 bg-white p-8 rounded-2xl shadow-2xl max-w-md mx-auto">
      <h2 className="text-3xl font-extrabold flex justify-center items-center gap-2">
        {isPerfectScore ? (
          <span className="text-green-600">🎉 Perfect Score!</span>
        ) : (
          <span className="text-blue-600">🎯 Quiz Complete!</span>
        )}
      </h2>

      <p className="text-xl text-gray-800">
        🏆 Score: <span className="font-semibold text-blue-600">{score}</span> out of {totalQuestions}
      </p>

      <p className="text-md text-gray-600">
        ✅ Questions Answered: <span className="font-medium">{answeredQuestions}</span> <br />
        ⏱️ Time Spent: <span className="font-medium">{formattedTime}</span>
      </p>

      <div className="flex gap-4 justify-center">
        <button
          onClick={onRetry}
          disabled={isLoggingOut}
          className={`px-6 py-3 bg-gradient-to-r from-blue-500 to-indigo-500 text-white text-lg rounded-xl font-semibold shadow-md hover:from-blue-600 hover:to-indigo-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400 ${
            isLoggingOut ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          🔄 Try Again
        </button>

        <button
          onClick={async () => {
            setIsLoggingOut(true);
            await handleLogout();
          }}
          disabled={isLoggingOut}
          className={`px-6 py-3 bg-gradient-to-r from-red-500 to-red-600 text-white text-lg rounded-xl font-semibold shadow-md hover:from-red-600 hover:to-red-700 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-red-400 ${
            isLoggingOut ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          {isLoggingOut ? '🔄 Logging out...' : '🚪 Logout'}
        </button>
      </div>

      {!isPerfectScore && (
        <p className="text-sm text-gray-500 mt-4">
          ✨ Try again to achieve a perfect score!
        </p>
      )}
    </div>
  );
};

export default Result;
