import React, { useState, useEffect, useRef } from 'react';
import Login from './components/Login';
import Quiz from './components/Quiz';
import { fetchQuizQuestions } from './service/quizService';
import './styles/index.css';
import { Question } from './types';

function App() {
  const [user, setUser] = useState<string | null>(() => {
    return localStorage.getItem('user');
  });
  const [questions, setQuestions] = useState<Question[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [timeSpent, setTimeSpent] = useState<number>(0);
  const [quizKey, setQuizKey] = useState<number>(0);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Timer functions
  const startTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }
    setTimeSpent(0);
    timerRef.current = setInterval(() => {
      setTimeSpent(prev => prev + 1);
    }, 1000);
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }
  };

  // Cleanup timer on component unmount
  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  useEffect(() => {
    const loadQuestions = async () => {
      try {
        setLoading(true);
        const fetchedQuestions = await fetchQuizQuestions(10);
        setQuestions(fetchedQuestions);
        startTimer();
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      loadQuestions();
    }
  }, [user]);

  const handleLogin = async (username: string) => {
    try {
      if (!username.trim()) {
        throw new Error('Username cannot be empty');
      }

      localStorage.setItem('user', username.trim());
      setUser(username.trim());

      setLoading(true);
      const fetchedQuestions = await fetchQuizQuestions(10);
      setQuestions(fetchedQuestions);
      setError(null);
      startTimer();
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Login failed');
      localStorage.removeItem('user');
      setUser(null);
      setQuestions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    // Check quiz completion status
    const quizProgress = localStorage.getItem('quizProgress');
    const progressData = quizProgress ? JSON.parse(quizProgress) : null;
    const isQuizComplete = progressData?.score === questions.length;

    if (!isQuizComplete) {
      setShowLogoutWarning(true);
      return;
    }

    // Proceed with logout if quiz is complete
    try {
      stopTimer();
      localStorage.removeItem('user');
      localStorage.removeItem('quizProgress');
      localStorage.removeItem('timeSpent');

      setUser(null);
      setQuestions([]);
      setLoading(false);
      setError(null);
      setTimeSpent(0);
    } catch (err) {
      console.error('Error during logout:', err);
      setUser(null);
      setQuestions([]);
    }
  };

  const handleCloseWarning = () => {
    setShowLogoutWarning(false);
  };

  const handleTryAgain = async () => {
    try {
      setError(null);
      const fetchedQuestions = await fetchQuizQuestions(10);
      setQuestions(fetchedQuestions);
      setQuizKey(prev => prev + 1);
      startTimer();
    } catch (err) {
      setError('Failed to load questions. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl text-red-600">{error}</div>
        <button 
          onClick={handleTryAgain} 
          className="ml-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Try Again
        </button>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading questions...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-purple-100 py-10 flex flex-col justify-center items-center">
      <div className="w-full max-w-2xl px-6">
        <div className="bg-white shadow-2xl rounded-3xl p-8 sm:p-12 transition-all duration-300">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-semibold text-gray-800">
              👋 Welcome, <span className="text-blue-600">{user}</span>!
            </h2>
            <button 
              onClick={handleLogout}
              className="px-4 py-2 bg-red-500 text-white text-sm rounded-lg shadow-md hover:bg-red-600 hover:shadow-lg focus:outline-none focus:ring-2 focus:ring-red-300 transition-all"
            >
              🚪 Logout
            </button>
          </div>

          <hr className="mb-6 border-gray-200" />

         <Quiz 
  key={quizKey}
  questions={questions} 
  timeLimit={300} 
  timeSpent={timeSpent} 
  onTryAgain={handleTryAgain}
  onLogout={handleLogout} // Add this line
/>

          {/* Logout Warning Dialog */}
          {showLogoutWarning && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg p-6 max-w-md mx-4 shadow-xl">
                <div className="text-xl font-semibold text-red-600 mb-4">
                  ⚠️ Cannot Logout
                </div>
                <p className="text-gray-700 mb-6">
                  Please complete all questions with a perfect score (10/10) before logging out.
                </p>
                <div className="flex justify-end">
                  <button
                    onClick={handleCloseWarning}
                    className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                  >
                    Continue Quiz
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
