import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Quiz from './components/Quiz';
import { fetchQuizQuestions } from './service/quizService';
import './styles/index.css';
import { Question } from './types';

function App() {
  const [user, setUser] = useState<string | null>(() => localStorage.getItem('user'));
  const [questions, setQuestions] = useState<Question[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  useEffect(() => {
    const loadQuestions = async () => {
      try {
        setLoading(true);
        setError(null);
        const fetchedQuestions = await fetchQuizQuestions(10);
        setQuestions(fetchedQuestions);
      } catch (err) {
        const errorMessage = err instanceof Error ? err.message : 'Failed to load questions. Please try again later.';
        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    };


    if (user) {
      loadQuestions();
    }
  }, [user, retryCount]); // Add retryCount as dependency

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
  };

  const handleLogin = (username: string) => {
    localStorage.setItem('user', username);
    setUser(username);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setQuestions([]);
  };

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="flex flex-col items-center space-y-4">
          <div className="text-xl text-red-600">{error}</div>
          <button 
            onClick={handleRetry}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading questions...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="flex justify-between items-center mb-4">
            <div>Welcome, {user}!</div>
            <button 
              onClick={handleLogout}
              className="px-4 py-2 text-sm bg-red-500 text-white rounded hover:bg-red-600"
            >
              Logout
            </button>
          </div>
          <Quiz questions={questions} timeLimit={300} />
        </div>
      </div>
    </div>
  );
}

export default App;
