import React, { useState, useEffect } from "react";
import { Question } from "../types";
import Result from "./Result";
import { saveQuizState, loadQuizState } from "../utils/storage";


interface QuizState {
  currentQuestion: number;
  score: number;
  timeLeft: number;
  answers: string[];
  isComplete: boolean;
  showResult: boolean; // Add this line
}

interface QuizProps {
  questions: Question[];
  timeLimit: number; // in seconds
}

const Quiz: React.FC<QuizProps> = ({ questions, timeLimit }) => {
  const [state, setState] = useState<QuizState>(() => {
    const saved = loadQuizState();
    return (
      saved || {
        currentQuestion: 0,
        score: 0,
        timeLeft: timeLimit,
        answers: [],
        isComplete: false,
        showResult: false,
      }
    );
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setState((prev) => {
        if (prev.timeLeft <= 0 || prev.isComplete) {
          clearInterval(timer);
          return { ...prev, isComplete: true, showResult: true };
        }
        return { ...prev, timeLeft: prev.timeLeft - 1 };
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    saveQuizState(state);
  }, [state]);

  const handleAnswer = (answer: string) => {
    const isCorrect = answer === questions[state.currentQuestion].correctAnswer;
    setState((prev) => ({
      ...prev,
      score: isCorrect ? prev.score + 1 : prev.score,
      answers: [...prev.answers, answer],
      currentQuestion: prev.currentQuestion + 1,
      isComplete: prev.currentQuestion + 1 >= questions.length,
      showResult: prev.currentQuestion + 1 >= questions.length,
    }));
  };

  const handleRetry = () => {
    setState({
      currentQuestion: 0,
      score: 0,
      timeLeft: timeLimit,
      answers: [],
      isComplete: false,
      showResult: false,
    });
  };

  if (state.showResult || state.timeLeft <= 0) {
    return (
      <Result
        score={state.score}
        totalQuestions={questions.length}
        answeredQuestions={state.answers.length}
        timeSpent={timeLimit - state.timeLeft}
        onRetry={handleRetry}
      />
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          Question {state.currentQuestion + 1}/{questions.length}
        </div>
        <div>
          Time Left: {Math.floor(state.timeLeft / 60)}:
          {(state.timeLeft % 60).toString().padStart(2, "0")}
        </div>
      </div>
      <div className="text-xl">{questions[state.currentQuestion].question}</div>
      <div className="space-y-3">
        {questions[state.currentQuestion].options.map((option) => (
          <button
            key={option}
            onClick={() => handleAnswer(option)}
            className="w-full p-4 text-left bg-white border rounded-lg hover:bg-gray-50"
          >
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Quiz;
