import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Quiz from './components/Quiz';
import { fetchQuizQuestions } from './service/quizService';
import './styles/index.css';
import { Question } from './types';

function App() {
  const [user, setUser] = useState<string | null>(() => {
    // Add persistence for user login
    return localStorage.getItem('user');
  });
  const [questions, setQuestions] = useState<Question[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadQuestions = async () => {
      try {
        setLoading(true);
        const fetchedQuestions = await fetchQuizQuestions(10);
        setQuestions(fetchedQuestions);
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {  // Only load questions when user is logged in
      loadQuestions();
    }
  }, [user]);  // Add user as dependency

    const handleLogin = async (username: string) => {
    try {
      if (!username.trim()) {
        throw new Error('Username cannot be empty');
      }

      // Save to localStorage
      localStorage.setItem('user', username.trim());

      // Set user state
      setUser(username.trim());

      // Load questions immediately after successful login
      setLoading(true);
      const fetchedQuestions = await fetchQuizQuestions(10);
      setQuestions(fetchedQuestions);
      setError(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Login failed');
      // Clear any partial state
      localStorage.removeItem('user');
      setUser(null);
      setQuestions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    try {
      // Clear all quiz-related data
      localStorage.removeItem('user');
      localStorage.removeItem('quizProgress'); // If you have any quiz progress stored
      localStorage.removeItem('timeSpent'); // If you have time tracking

      // Reset all states
      setUser(null);
      setQuestions([]);
      setLoading(false);
      setError(null);

      // Optional: Force reload to ensure clean state
      // window.location.reload();
    } catch (err) {
      console.error('Error during logout:', err);
      // Even if there's an error, try to clear the state
      setUser(null);
      setQuestions([]);
    }
  };

// Add new state for time spent
const [timeSpent, setTimeSpent] = useState<number>(0);
const timerRef = useRefef<NodeJS.Timeout | null>(null);

// Add timer functions
const startTimer = () => {
  if (timerRef.current) {
    clearInterval(timerRef.current);
  }
  setTimeSpent(0);
  timerRef.current = setInterval(() => {
    setTimeSpent(prev => prev + 1);
  }, 1000);
};


  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl text-red-600">{error}</div>
        <button 
          onClick={() => window.location.reload()} 
          className="ml-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Try Again
        </button>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading questions...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="flex justify-between items-center mb-4">
            <div>Welcome, {user}!</div>
            <button 
              onClick={handleLogout}
              className="px-4 py-2 text-sm bg-red-500 text-white rounded hover:bg-red-600"
            >
              Logout
            </button>
          </div>
          <Quiz questions={questions} timeLimit={300} />
        </div>
      </div>
    </div>
  );
}

export default App;
