import React from 'react';

interface ResultProps {
  score: number;
  totalQuestions: number;
  answeredQuestions: number;
  timeSpent: number;
  onRetry: () => void;
  resetTimer: () => void; // Add new prop for resetting timer
}

const Result: React.FC<ResultProps> = ({ 
  score, 
  totalQuestions, 
  answeredQuestions, 
  timeSpent, 
  onRetry,
  resetTimer 
}) => {

  const handleRetry = () => {
    resetTimer(); // Reset the timer first
    onRetry(); // Then trigger the retry logic
  };

  return (
    <div className="text-center space-y-6 bg-white p-8 rounded-2xl shadow-2xl max-w-md mx-auto">
      <h2 className="text-3xl font-extrabold text-green-600 flex justify-center items-center gap-2">
        üéâ Quiz Complete!
      </h2>

      <p className="text-xl text-gray-800">
        üèÜ <span className="font-semibold text-blue-600">{score}</span> out of {totalQuestions}
      </p>

      <p className="text-md text-gray-600">
        ‚úÖ Answered: <span className="font-medium">{answeredQuestions}</span> <br />
        ‚è±Ô∏è Time Spent: <span className="font-medium">{timeSpent}s</span>
      </p>

      <button
        onClick={handleRetry}
        className="px-6 py-3 bg-gradient-to-r from-blue-500 to-indigo-500 text-white text-lg rounded-xl font-semibold shadow-md hover:from-blue-600 hover:to-indigo-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
      >
        üîÅ Try Again
      </button>
    </div>
  );
};

export default Result;
